-- DND35e.35e_stat_block definition
DROP TABLE IF EXISTS `35e_stat_block`;
CREATE TABLE `35e_stat_block` (
  `id` double NOT NULL,
  `name` varchar(100) DEFAULT NULL,
  `altname` varchar(100) DEFAULT NULL,
  `size` varchar(255) DEFAULT NULL,
  `type` varchar(255) DEFAULT NULL,
  `descriptor` varchar(100) DEFAULT NULL,
  `hit_dice` varchar(100) DEFAULT NULL,
  `initiative` varchar(100) DEFAULT NULL,
  `speed` varchar(255) DEFAULT NULL,
  `armor_class` longtext DEFAULT NULL,
  `base_attack` varchar(100) DEFAULT NULL,
  `grapple` varchar(100) DEFAULT NULL,
  `attack` longtext DEFAULT NULL,
  `full_attack` longtext DEFAULT NULL,
  `space` varchar(100) DEFAULT NULL,
  `reach` double DEFAULT NULL,
  `special_attacks` double DEFAULT NULL,
  `special_qualities` longtext DEFAULT NULL,
  `saves` varchar(100) DEFAULT NULL,
  `abilities` varchar(255) DEFAULT NULL,
  `skills` longtext DEFAULT NULL,
  `bonus_feats` varchar(100) DEFAULT NULL,
  `feats` longtext DEFAULT NULL,
  `epic_feats` longtext DEFAULT NULL,
  `challenge_rating` varchar(100) DEFAULT NULL,
  `alignment` varchar(100) DEFAULT NULL,
  `reference` varchar(100) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

